CoordinatesPrecedence.map:
function (doc) {
    if(doc.rows.length > 0) {
        for(var idx in doc.rows){
          if(doc.rows[idx].doc.coordinates!=null){
              emit(doc.rows[idx].doc.coordinates,1);
           }
            else{
              emit(doc.rows[idx].doc.place,1);
            }
        }
    }
}
